#!/usr/bin/python3
if __name__ == '__main__':
    """ A endless loop that iterates over a list of words and stores words. """    
    words = []
    while True:
        word = input("Enter a word: ")
        if not word:
            break
        words.append(word)
    print(" ".join(words))
