if __name__ == '__main__':
    """ A function that multiplyes user input and multiplyes fruits. """    
    fruits = input("Enter the fruits: ")
    fruits = fruits.split()
    fruits = map(int, fruits)
    fruits = list(fruits)
    fruits = sum(fruits)
    print(fruits)
